var inp = document.createElement('input'), ide = this.parent.parent, result=0, done=false; function userImport() { function txtOnlyMsg(ftype, anyway) { ide.confirm( localize( 'Snap! can only import "text" files.
' + 'You selected a file of type "' + ftype + '".' ) + '

' + localize('Open anyway?'), 'Unable to import', anyway // callback ); } function readText(aFile) { var frd = new FileReader(), ext = aFile.name.split('.').pop().toLowerCase(); function isTextFile(aFile) { // special cases for Windows // check the file extension for text-like-ness return aFile.type.indexOf('text') !== -1 || contains(['txt', 'csv', 'xml', 'json', 'tsv'], ext); } function isType(aFile, string) { return aFile.type.indexOf(string) !== -1 || (ext === string); } frd.onloadend = function (e) { done=true; if (isType(aFile, 'csv')) { result=Process.prototype.parseCSV(e.target.result);} else if (isType(aFile, 'json')) { result=Process.prototype.parseJSON(e.target.result);} else { result=e.target.result;} }; if (isTextFile(aFile)) { frd.readAsText(aFile); } else { txtOnlyMsg( aFile.type, function () {frd.readAsText(aFile); } ); } } document.body.removeChild(inp); ide.filePicker = null; if (inp.files.length > 0) { readText(inp.files[inp.files.length - 1]); } } if (ide.filePicker) { document.body.removeChild(ide.filePicker); ide.filePicker = null; } inp.type = 'file'; inp.style.color = "transparent"; inp.style.backgroundColor = "transparent"; inp.style.border = "none"; inp.style.outline = "none"; inp.style.position = "absolute"; inp.style.top = "0px"; inp.style.left = "0px"; inp.style.width = "0px"; inp.style.height = "0px"; inp.style.display = "none"; inp.addEventListener( "change", userImport, false ); document.body.appendChild(inp); ide.filePicker = inp; inp.click(); return function(){return new List([done,result]);};
